mapper.update:
UPDATE SM_product
product_id = #{productId}, product_description = #{productDescription}, product_stock = #{productStock}, modify_date = #{modifyDate}, min_age = #{minAge}, max_age = #{maxAge}
WHERE product_id=#{productId}

mapper.select:
SELECT product_id AS "productId"
FROM SM_product
WHERE product_id=#{value}
